mapper.select:
select a.demand_id, b.corp_name as operationuser,a.project_name,a.technology_direction,a.demand_years,a.education,a.demand_number,a.specificrequi_rement,a.project_location,a.address,a.direct_worklife,a.target_number,a.important_level,a.min_salary,a.max_salary,a.technical_requirement,a.Langue,a.demand_pic,a.interviewer,a.contact_phone,a.specific_location,a.attention,a.if_look,a.chsi_flag,a.coopnature,a.state,a.close_date,a.temp_id,a.temp_name,a.add_time,a.operationuser,a.oper_username,a.bz from mar_demand a
LEFT JOIN mar_company b on a.corp_code=b.corp_code

     and a.corp_code = #{corpCode}
     and a.project_name like concat('%', #{projectName}, '%')
     and a.technology_direction = #{technologyDirection}
     and a.demand_years = #{demandYears}
     and a.education = #{education}
     and a.demand_number = #{demandNumber}
     and a.specificrequi_rement = #{specificrequiRement}
     and a.project_location = #{projectLocation}
     and a.address = #{address}
     and a.direct_worklife = #{directWorklife}
     and a.target_number = #{targetNumber}
     and a.important_level = #{importantLevel}
     and a.min_salary = #{minSalary}
     and a.max_salary = #{maxSalary}
     and a.technical_requirement = #{technicalRequirement}
     and a.Langue = #{langue}
     and a.demand_pic = #{demandPic}
     and a.interviewer = #{interviewer}
     and a.contact_phone = #{contactPhone}
     and a.specific_location = #{specificLocation}
     and a.attention = #{attention}
     and a.if_look = #{ifLook}
     and a.chsi_flag = #{chsiFlag}
     and a.coopnature = #{coopnature}
     and a.state = #{state}
     and a.close_date = #{closeDate}
     and a.temp_id = #{tempId}
     and a.temp_name like concat('%', #{tempName}, '%')
     and a.add_time = #{addTime}
     and a.operationuser = #{operationuser}
     and a.oper_username like concat('%', #{operUsername}, '%')

order  by a.add_time desc

mapper.update:
update mar_demand
corp_code = #{corpCode}, project_name = #{projectName}, technology_direction = #{technologyDirection}, demand_years = #{demandYears}, education = #{education}, demand_number = #{demandNumber}, specificrequi_rement = #{specificrequiRement}, project_location = #{projectLocation}, address = #{address}, direct_worklife = #{directWorklife}, target_number = #{targetNumber}, important_level = #{importantLevel}, min_salary = #{minSalary}, max_salary = #{maxSalary}, technical_requirement = #{technicalRequirement}, Langue = #{langue}, demand_pic = #{demandPic}, interviewer = #{interviewer}, contact_phone = #{contactPhone}, specific_location = #{specificLocation}, attention = #{attention}, if_look = #{ifLook}, chsi_flag = #{chsiFlag}, coopnature = #{coopnature}, state = #{state}, close_date = #{closeDate}, temp_id = #{tempId}, temp_name = #{tempName}, add_time = #{addTime}, operationuser = #{operationuser}, oper_username = #{operUsername},
where demand_id = #{demandId}

mapper.select:
SELECT d.audited_path as bz,b.* from mar_sign a LEFT JOIN mar_demand b on a.demand_id=b.demand_id
LEFT JOIN mar_auditedtor_demand c on b.demand_id=c.demand_id
LEFT JOIN  mar_auditeditor d on c.audited_id= d.audited_id
where  1 = 1 and b.state=0
and a.dept_id = #{deptId} 
 and b.project_name like concat('%', #{projectName}, '%')
 and b.technology_direction = #{technologyDirection}
 and b.demand_years = #{demandYears}
 and b.project_location = #{projectLocation}
 and b.important_level = #{importantLevel}
 and b.corp_code = #{corpCode}
 order by b.add_time desc

SELECT b.demand_id ,b.project_name from mar_sign a LEFT JOIN mar_demand b on a.demand_id=b.demand_id
where  1 = 1 and b.state=0
and a.dept_id = #{deptId}
