mapper.update:
update mar_demandresume
demand_id = #{demandId}, customer_code = #{customerCode}, bind_time = #{bindTime}, bind_people = #{bindPeople}, download_status = #{downloadStatus}, is_recommend=#{isRecommend}, newfollowtime = #{newfollowtime}, trackz_peoname = #{trackzPeoname}, resume_enclosurepath = #{resumeEnclosurepath},
where id = #{id}

mapper.select:
select id, demand_id, customer_code, bind_time, bind_people,
download_status, newfollowtime, trackz_peoname, resume_enclosurepath
from mar_demandresume a
where a.bind_time <#{bindTime}
and a.demand_id = #{demandId}
